mapper.update:
UPDATE QA
SET DELETED ='1' 
WHERE QANO = #{ qaNo }

UPDATE QA
SET COMPLETED ='1' 
WHERE QANO = #{ qaNo }

mapper.select:
SELECT QANO, COMMENTNO, CONTENT, REGDATE, MEMBERID
FROM QACOMMENT
WHERE QANO = #{ qaNo }

mapper.update:
UPDATE QA
SET COMPLETED ='0' 
WHERE QANO = #{ qaNo }
