mapper.select:
SELECT MEMBERID
FROM MEMBER
WHERE MEMBERID = #{ memberId }

SELECT MEMBERID
FROM MEMBER
WHERE EMAIL = #{ email }

mapper.update:
UPDATE MEMBER
SET PWD = #{ pwd }
WHERE MEMBERID = #{ memberId }

