mapper.update:
UPDATE QA
SET DELETED ='1' 
WHERE QANO = #{ qaNo }

UPDATE QA
SET COMPLETED ='1' 
WHERE QANO = #{ qaNo }

mapper.select:
SELECT QANO, COMMENTNO, COMMENT, REGDATE
FROM QACOMMENT
WHERE QANO = #{ qaNo }

