mapper.update:
UPDATE REVIEW
SET TITLE = #{ title }
	, CONTENT = #{ content }
	, CATEGORY = #{ category }
WHERE REVIEWNO = #{ reviewNo }

UPDATE REVIEWFILE
SET SAVEDFILENAME = #{ savedFileName }, USERFILENAME = #{ userFileName }
WHERE REVIEWNO = #{ reviewNo } AND FLAG = 1

mapper.select:
SELECT REVIEWFILENO, SAVEDFILENAME, USERFILENAME, REVIEWNO
FROM REVIEWFILE
WHERE REVIEWFILENO = #{ reviewFileNo }

mapper.update:
UPDATE BUY
SET FLAG = 1
WHERE BUYNO = #{ buyNo }

UPDATE RENTAL
SET FLAG = 1
WHERE RENTNO = #{ rentNo }

UPDATE RENTAL
SET FLAG = 0
WHERE RENTNO = #{ rentNo }

UPDATE PTREVIEW
SET TITLE = #{ title }
	, CONTENT = #{ content }
WHERE PTREVIEWNO = #{ ptreviewNo }

UPDATE PTREVIEWFILE
SET SAVEDFILENAME = #{ savedFileName }, USERFILENAME = #{ userFileName }
WHERE PTREVIEWNO = #{ ptreviewNo } AND FLAG = 1

UPDATE BUY
SET FLAG = 0
WHERE BUYNO = #{ buyNo }
